def process_large_csv(file_path):
    """Ingest user data with error tracking"""
    with open(file_path, 'r') as f:
        # üöÄ PRODUCTION USE CASE:
        # Using enumerate to track line numbers for logs
        for line_num, row in enumerate(f, start=1):
            try:
                user = parse_user(row)
                db.save(user)
            except ValueError as e:
                # Crucial for debugging production logs:
                logger.error(f"Failed to parse line {line_num}: {e}")
